import React from 'react'
import PropTypes from 'prop-types'
import theme from '../../theme'
import { P } from '../../Typography'

const style = (props) => {
  let marginTop = theme.whitespaceS

  if (props.marginTopM) {
    marginTop = theme.whitespaceM
  }
  return {
    borderTop: `1px solid ${theme.grey}`,
    marginBottom: '0px',
    marginTop,
    ...props.style,
  }
}

const BorderWithText = props => (
  <div style={style(props)}>
    <P extraSmall noMargin greyMedium style={props.textStyle} >
      {props.children}
    </P>
  </div>
)

BorderWithText.defaultProps = { textStyle: undefined }

BorderWithText.propTypes = {
  children: PropTypes.node.isRequired,
  textStyle: PropTypes.object,
}

export default BorderWithText
