import React from 'react'
import { storiesOf } from '@storybook/react'
import { withKnobs, color, text } from '@storybook/addon-knobs'
import withReadme from 'storybook-readme/with-readme'
import { Col } from 'react-bootstrap'
import BorderWithText from './BorderWithText'
import readme from './readme.md'

storiesOf('Atoms/Borders', module).addDecorator(withKnobs)
  .add(
    'Border With text',
    withReadme(readme, () => (
      <Col style={{ marginTop: '30px' }} xs={6} >
        <BorderWithText
          style={{ borderTop: `1px solid ${color('Color', '#e6e6e6')}` }}
          textStyle={{ color: color('Tet color', '#bfbfbf') }}
        >
          {text('text', 'hello')}
        </BorderWithText>
      </Col>
    )),
  )
