import React from 'react'
import theme from '../../theme'

const style = (props) => {
  if (props.extraMargin) {
    return {
      borderBottom: `1px solid ${theme.grey}`,
      marginBottom: theme.whitespaceM,
      marginTop: theme.whitespaceM,
      ...props.style,
    }
  }
  return {
    borderBottom: `1px solid ${theme.grey}`,
    marginBottom: theme.whitespaceS,
    ...props.style,
  }
}

const Border = props => <div style={style(props)} />

export default Border
