import { expect } from 'chai'
import sinon from 'sinon'
import { shallow } from 'enzyme'
import { describe, it, beforeEach } from 'storybook-addon-specifications'
import React from 'react'
import theme from '../../theme'
import RadioButton from './RadioButton'

const test = () => (
  describe('RadioButton default', () => {
    let wrapper
    let onClick
    beforeEach(() => {
      onClick = sinon.spy()
      wrapper = shallow(<RadioButton onClick={onClick}>Radio</RadioButton>)
    })
    it('renders the component', () => {
      expect(wrapper).to.exist
    })
    it('should contain an onClick props', () => (
      expect(wrapper.find('Button').prop('onClick')).to.exist
    ))
    it('the onClick props should be a function', () => {
      expect(wrapper.find('Button').prop('onClick')).to.be.a('function')
    })
    it('when clicked it should call the onClick function', () => {
      wrapper.find('Button').simulate('click')
      expect(onClick.callCount).to.equal(1)
    })
    it('should change the backgroundColor to fixico grey when hover', () => {
      expect(wrapper.prop('style').backgroundColor).to.equal('transparent')
      wrapper.setState({ hover: true })
      expect(wrapper.prop('style').backgroundColor).to.equal(theme.grey)
    })
    it('when disabled should contain the prop disabled', () => {
      wrapper.setProps({ disabled: true })
      expect(wrapper.find('Button').prop('disabled')).to.exist
    })
    it('when disabled should not be able to click the button', () => {
      wrapper.setProps({ disabled: true })
      wrapper.find('Button').simulate('click')
      expect(onClick.callCount).to.equal(0)
    })
    it('when selected should contain the prop selected', () => {
      wrapper.setProps({ selected: true })
      expect(wrapper.find('Button').prop('selected')).to.exist
    })
    it('when selected the backgroundColor should be theme grey', () => {
      wrapper.setProps({ selected: true })
      expect(wrapper.prop('style').backgroundColor).to.equal(theme.grey)
    })
  })
)

export default test
