import React from 'react'
import { storiesOf } from '@storybook/react'
import { withKnobs, color, text, boolean, object } from '@storybook/addon-knobs'
import withReadme from 'storybook-readme/with-readme'
import { Col } from 'react-bootstrap'
import { specs } from 'storybook-addon-specifications'
import theme from '../../theme'
import TransparentButton from './TransparentButton'
import test from './TransparentButton.test'
import readme from './readme.md'

storiesOf('Atoms/Buttons', module).addDecorator(withKnobs)
  .add(
    'Transparent',
    withReadme(readme, () => {
      const story = (
        <Col xs={6}>
          <TransparentButton
            style={{
              color: color('text color', theme.white),
              backgroundColor: color('background', 'rgba(0,0,0, 0.7)'),
              ':hover': object('hover changes', { backgroundColor: theme.greenDark }),
            }}
            disabled={boolean('disabled', false)}
            onClick={() => { /* no-op */ }}
          >
            {text('text', 'Hello')}
          </TransparentButton>
        </Col>
      )
      specs(test)
      return story
    }),
  )
