import React from 'react'
import PropTypes from 'prop-types'
import theme from '../theme'

const styles = props => ({
  color: theme.errorMedium,
  marginTop: '5px',
  ...props.style,
})

const FeedbackInput = props => (
  <span style={styles(props)} className="form-control-feedback glyphicon">
    <img src={`https://website-assets.imgix.net/icon/${props.status}.svg`} alt="validation icon" />
  </span>
)

FeedbackInput.propTypes = {
  status: PropTypes.string.isRequired,
}

export default FeedbackInput
