import React from 'react'
import { storiesOf } from '@storybook/react'
import { Col } from 'react-bootstrap'
import withReadme from 'storybook-readme/with-readme'
import { specs } from 'storybook-addon-specifications'
import { withKnobs, text, object, color } from '@storybook/addon-knobs'
import theme from '../theme'
import test from './Link.test'
import A from './Link'
import readme from './readme.md'

storiesOf('Atoms/Link', module).addDecorator(withKnobs)
  .add(
    'Link',
    withReadme(readme, () => {
      specs(test)
      return (
        <Col xs={8}>
          <A
            href={text('href', 'mailto:info@fixico.nl')}
            onClick={() => 'link'}
            style={object('style', { color: color('color', theme.green) })}
          >
            {text('text', 'Cool Link')}
          </A>
        </Col>
      )
    }),
  )
