import React from 'react'
import { Row, Col } from 'react-bootstrap'
import { storiesOf } from '@storybook/react'
import withReadme from 'storybook-readme/with-readme'
import { withKnobs, text } from '@storybook/addon-knobs'
import { specs } from 'storybook-addon-specifications'
import Icon from './Icon'
import test from './Icon.test'
import readme from './readme.md'

const iconNames = ['phone_icon', 'email_icon', 'help_icon', 'user_icon', 'chat_icon', 'hamburger_icon', 'exit_icon', 'flag_icon', 'star_icon', 'stopwatch_icon', 'filter_icon', 'arrow_open']

storiesOf('Quarks/React svgs', module).addDecorator(withKnobs)
  .add(
    'Main Icons',
    withReadme(readme, () => {
      specs(test)
      return (
        <Row>
          {iconNames.map(icon => (
            <Col key={icon} xs={4}>
              <Icon
                name={icon}
                viewBox="0 0 90 90"
                style={() => ({ height: '90px', fill: text('Change color on hover', 'black') })}
              />
            </Col>
          ))}
        </Row>
      )
    }),
  )
