import { expect } from 'chai'
import { shallow } from 'enzyme'
import { describe, it, beforeEach } from 'storybook-addon-specifications'
import React from 'react'
import RatingTag from './RatingTag'

const test = () => (
  describe('RatingTag', () => {
    let wrapper
    beforeEach(() => {
      wrapper = shallow(<RatingTag
        rating="9.0"
        text="Good"
      />)
    })
    it('renders the component', () => {
      expect(wrapper).to.exist
    })
    it('has prop rating', () => {
      expect(wrapper.find('Header').at(0).props().children).to.equal('9.0')
    })
    it('has prop text', () => {
      expect(wrapper.find('Header').at(1).props().children).to.equal('Good')
    })
    it('has an star icon', () => {
      expect(wrapper.find('Icon').props().name).to.equal('star_icon')
    })
  })
)

export default test
