import { expect } from 'chai'
import { describe, it } from 'storybook-addon-specifications'
import { shallow } from 'enzyme'
import React from 'react'
import PointingTitle from './PointingTitle'

const test = () => (
  describe('Pointing Component', () => {
    const wrapper = shallow(<PointingTitle color="red" >
                              Hello
                            </PointingTitle>)
    it('Should render an p', () =>
      expect(wrapper.find('Header').dive().type()).to.equal('p'))
    it('Should render an h5 style', () =>
      expect(wrapper.find('Header').dive().prop('style').fontSize).to.equal('16px'))
    it('Should render an contains the right title', () =>
      expect(wrapper.find('Header').get(0).props.children).to.equal('Hello'))
    it('Should render an arrow', () =>
      expect(wrapper.childAt(1).prop('style')).to.have.deep.include({ left: '50%', bottom: '-10px', borderTopColor: 'red' }))
  })
)

export default test
