import { expect } from 'chai'
import { shallow } from 'enzyme'
import { describe, it } from 'storybook-addon-specifications'
import React from 'react'
import theme from './../../theme'
import Header from './Header'

const test = () => (
  describe('Headers', () => {
    const wrapper = shallow(<Header tag="H1">Main H1</Header>)
    it('should render the right text', () =>
      expect(wrapper.find('p').text()).to.equal('Main H1'))
    it('shoul render H1 size', () => {
      wrapper.setProps({ tag: 'H1' })
      expect(wrapper.find('p').props().style).to.have.deep.property('fontSize', theme.fontSizeXxxl)
    })
    it('shoul render H2 size', () => {
      wrapper.setProps({ tag: 'H2' })
      expect(wrapper.find('p').props().style).to.have.deep.property('fontSize', theme.fontSizeXxl)
    })
    it('shoul render H3 size', () => {
      wrapper.setProps({ tag: 'H3' })
      expect(wrapper.find('p').props().style).to.have.deep.property('fontSize', theme.fontSizeXl)
    })
    it('shoul render H4 size', () => {
      wrapper.setProps({ tag: 'H4' })
      expect(wrapper.find('p').props().style).to.have.deep.property('fontSize', theme.fontSizeL)
    })
    it('shoul render H5 size', () => {
      wrapper.setProps({ tag: 'H5' })
      expect(wrapper.find('p').props().style).to.have.deep.property('fontSize', theme.fontSizeM)
    })
    it('shoul render H6 size', () => {
      wrapper.setProps({ tag: 'H6' })
      expect(wrapper.find('p').props().style).to.have.deep.property('fontSize', theme.fontSizeS)
    })
  })
)

export default test
