import React from 'react'
import { expect } from 'chai'
import { shallow } from 'enzyme'
import { describe, it } from 'storybook-addon-specifications'
import Label from './Label'

const test = () => (
  describe('Label', () => {
    const wrapper = shallow(<Label>Test</Label>)
    it('should render the component', () => {
      expect(wrapper).to.exist
    })
    it('should contain a <label> element', () => {
      expect(wrapper.find('label')).to.have.length(1)
    })
    it('fontSize should be 12px default', () => {
      expect(wrapper.find('label').prop('style')).to.include({ fontSize: '12px' })
    })
    it('weight should be 700', () => {
      expect(wrapper.find('label').prop('style')).to.include({ fontWeight: 700 })
    })
    it('fontFamily should be Ubuntu', () => {
      expect(wrapper.find('label').prop('style')).to.include({ fontFamily: '"Ubuntu", sans-serif' })
    })
  })
)

export default test
